
public class RectangleTester {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Rectangle z = new Rectangle(5,10,15,20);
		
		System.out.println("The Area is: "+ z.calculateArea());
		System.out.println("The length and width are: " + z.length+ " and "+ z.width);
		
	}
}
